    ])
    return(
        <>
            <div className="col-12">
                <div className="col-6">
                    <Title name="CAUSES"/>
                </div>
                <div className="col-6">
                    <Search />
                    <CustomFilter />
                    <SortBy />
                </div>
            </div>
            <div className="col-12">
                {
                    cardInfo.map((info)=>{
                        return(<CauseCard info={info}/>)
                    })
                }

            </div>
        </>
    )
}
