import React, { useState } from 'react';
import './header.scss';
import CustomButton from '../CustomButton/CustomButton'
import Button from 'react-bootstrap/Button';
import SignIn from './SigIn-block/SignIn';
import { Container,Row,Col } from 'react-bootstrap';
import {Logo} from './style'; 

export default function Header() {
    const [headerItems, setheaderItems]=useState(['CAUSES','IMPACT','JOIN US','ABOUT US']);
    return (
                <div className="header">
                <div className="container-fluid">
                    <div className="col-12">
                        <div className="row">
                            <div className="col-12 col-lg-4 col-md-4 col-sm-4">
                            <Logo className="navbar-brand text-uppercase text-center" href="index.html">CSR X</Logo>
                            </div>
                            <div className="col-8 col-lg-8 col-md-8 col-sm-8">
                            <nav className="navbar navbar-expand-sm ">
                                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#menu">
                                <span className="navbar-toggler-icon"></span>
                                </button>
                                <div className="collapse navbar-collapse" id="menu">
                                    <ul className="navbar-nav fs-16 Montserrat-medium">
                                        {
                                        headerItems.map((item,index)=>{
                                        return (
                                        <li className="nav-item" key={index}>
                                        <a className="nav-link" href="#">{item}</a>
                                        </li>
                                        )
                                        })
                                        }  
                                        <li className="nav-item"><CustomButton color={"#707070"} padding={"10px 10px"} content="DONATE"/></li>
                                        <li className="nav-item dropdown"></li>
                                        <li className="nav-item dropdown">
                                        <a className="nav-link dropdown-toggle" href="#" id="navbardrop" data-toggle="dropdown">Sign In</a>
                                        <div className="dropdown-menu text-center">
                                            <a className="dropdown-item" href="index.html">Doner</a>
                                            <a className="dropdown-item" href="index.html">CSR Executive</a>
                                        </div>
                                        </li>
                                    </ul>
                                </div>
                            </nav>
                            </div>
                        </div>
                    </div>
                </div>
                </div>
    );
}
